mapper.select:
SELECT md.*, md.`work_time` workTime, mdai.head_portrait headPortrait, mh.`name` hospitalName
FROM medical_doctor md
join medical_doctor_field mdf on md.id = mdf.doctor_id and mdf.`field_id`=#{field}
join medical_doctor_department mdd on md.id = mdd.doctor_id and mdd.`department_id`=#{department}
LEFT JOIN `medical_doctor_authentication_information` mdai ON md.`authentication_information_id` = mdai.`id` LEFT JOIN `medical_hospital_doctor` mhd ON mhd.`doctor_id` = md.`id` AND mhd.`deleted`=0 LEFT JOIN `medical_hospital` mh ON mhd.`hospital_id` = mh.`id` WHERE md.deleted = 0 AND md.status = 1
and md.type = #{type}
and mh.id = #{hospitalId}
and md.name like concat('%',#{name},'%')
ORDER BY CONVERT(md.`name` USING gbk) ASC

SELECT work_time
FROM medical_doctor
WHERE id = #{doctorId}

SELECT md.*, md.`work_time` workTime, mdai.head_portrait headPortrait, mh.`name` hospitalName FROM medical_doctor md LEFT JOIN `medical_doctor_authentication_information` mdai ON md.`authentication_information_id` = mdai.`id` LEFT JOIN `medical_hospital_doctor` mhd ON mhd.`doctor_id` = md.`id` AND mhd.`deleted`=0 LEFT JOIN `medical_hospital` mh ON mhd.`hospital_id` = mh.`id` WHERE md.deleted = 0 AND md.status = 1 AND md.`recommend`=0
and md.type = #{type}
ORDER BY CONVERT(md.`name` USING gbk) ASC LIMIT #{offset},#{rows}

SELECT md.*, md.`work_time` workTime, mdai.head_portrait headPortrait, mh.`name` hospitalName FROM medical_doctor md LEFT JOIN `medical_doctor_authentication_information` mdai ON md.`authentication_information_id` = mdai.`id` LEFT JOIN `medical_hospital_doctor` mhd ON mhd.`doctor_id` = md.`id` AND mhd.`deleted`=0 LEFT JOIN `medical_hospital` mh ON mhd.`hospital_id` = mh.`id` WHERE md.deleted = 0 AND md.status = 1 AND md.`recommend`=1
and md.type = #{type}
ORDER BY md.`recommend_sort`desc LIMIT #{offset},#{rows}
