mapper.select:
SELECT mdp.*, oc.grade_name AS gradeName, oc.current_price*10 AS currentPrice, oc.smallimg_path as smallImgPath, oc.collection as collection, oc.start_time as startTime, oba.content as articleContent, oba.type_id as typeId, oba.title as articleTitle, oba.img_path as articleImgPath, if(oc.type =3,4,IF(oc.type = 1,3,if(oc.multimedia_type=1,1,2))) as courseType FROM medical_doctor_posts mdp LEFT JOIN oe_course oc ON oc.id = mdp.course_id LEFT JOIN oe_bxs_article oba ON oba.id = mdp.course_id WHERE mdp.deleted = 0 and mdp.status = 1 and mdp.doctor_id=#{doctorId}
and mdp.type = #{type}
ORDER BY mdp.stick desc,mdp.create_time desc

mapper.update:
UPDATE
medical_doctor_posts mdp
SET
mdp.`stick` = #{stick}
WHERE mdp.id = #{id}
