mapper.select:
select mh.id, mh.name, mh.province, mh.city, mh.detailed_address, mh.score, mh.authentication, mh.recommend, mhp.`picture` from medical_hospital mh LEFT JOIN `medical_hospital_picture` mhp ON mh.`id` = mhp.`hospital_id` where mh.id in
#{id}
ORDER BY mh.authentication DESC, mh.score DESC ,convert(mh.name using gbk) ASC

SELECT authentication
FROM medical_hospital
WHERE id = #{id}
