mapper.select:
SELECT
  oub.*
FROM
  oe_user_bank_card oub
WHERE oub.is_delete = 0

	AND oub.user_id = #{userId}


	AND oub.acct_pan = #{acctPan}


	AND oub.cert_id = #{certId}

SELECT oub.* FROM oe_user_bank_card oub WHERE oub.is_delete = 0
AND oub.user_id = #{userId}
ORDER BY oub.is_default ASC ,oub.create_time DESC
