mapper.select:
SELECT oc.id as id, oc.grade_name as gradeName, oc.smallimg_path as smallImgPath, oc.start_time as startTime, oc.end_time as endTime, oc.user_lecturer_id as userLecturerId, if(oc.type =3,4,IF(oc.type =1,3,if(oc.multimedia_type=1,1,2))) as type, if(oc.is_free =0,0,1) as watchState, if(oc.type =3,if(date_sub(oc.start_time,INTERVAL 1 DAY) >= now() or oc.start_time < NOW(),1,0),0) as cutoff,
if(oc.collection=0,oc.direct_id,(select o.direct_id from oe_course o join collection_course cc on o.id =cc.course_id where cc.collection_id =oc.id ORDER BY cc.collection_course_sort asc limit 1 )) as directId,
oc.collection as collection, oc.course_number as courseNumber, oc.course_detail as description, oc.course_outline as courseOutline,
if(oc.live_status = 2,if(DATE_ADD(now(),INTERVAL 10 MINUTE)>=oc.start_time and now()<oc.start_time,4, if(DATE_ADD(now(),INTERVAL 2 HOUR)>=oc.start_time and now()<oc.start_time,5,oc.live_status)),oc.live_status) as lineState,
oc.address as address, oc.city as city, oc.subtitle as subtitle, oc.course_length as courseLength, oc.current_price*10 as currentPrice, IFNULL((select count(*) from oe_criticize cc where cc.status = 1 and cc.course_id = oc.id ),0) as criticizeCount, IFNULL((SELECT ROUND(AVG(cc.overall_level), 1) FROM oe_criticize cc where cc.status = 1 and cc.course_id = oc.id and cc.overall_level !=0 ),0) as startLevel, oc.lecturer as name, oc.lecturer_description as lecturerDescription, ou.small_head_photo as headImg, ou.id as userId, ou.name as heir, ou.vhall_id as vhallId, oc.live_source_type as liveSourceType FROM oe_course as oc inner join oe_user as ou on oc.user_lecturer_id = ou.id where oc.id = #{courseId} and oc.is_delete=0

SELECT oc.id as id, oc.grade_name as gradeName, oc.smallimg_path as smallImgPath, oc.start_time as startTime, oc.end_time as endTime, oc.user_lecturer_id as userLecturerId, if(oc.type =3,4,IF(oc.type =1,3,if(oc.multimedia_type=1,1,2))) as type, if(oc.type =3,if(date_sub(oc.start_time,INTERVAL 1 DAY) >= now() or oc.start_time < NOW(),1,0),0) as cutoff, if(oc.is_free =0,0,1) as watchState, if(oc.type=1,ifnull((select SUM(count) from oe_gift_statement where receiver = user_lecturer_id),0),0) as giftCount,
if(oc.type=1,ifnull(( select count(*) from oe_focus of inner join oe_user as ozu on of.user_id = ozu.id where of.lecturer_id = oc.user_lecturer_id and ozu.is_delete = 0 and ozu.status = 0),0),0) as fansCount,
if(oc.type=1,ifnull(( select count(*) from oe_focus as of inner join oe_user as ou on of.lecturer_id = ou.id where of.user_id =oc.user_lecturer_id and ou.is_delete = 0 and ou.status = 0),0),0) as focusCount,
oc.course_length as courseLength, oc.collection as collection, oc.course_number as courseNumber, oc.course_detail as description, oc.course_outline as courseOutline,
if(oc.live_status = 2,if(DATE_ADD(now(),INTERVAL 10 MINUTE)>=oc.start_time and now()<oc.start_time,4, if(DATE_ADD(now(),INTERVAL 2 HOUR)>=oc.start_time and now()<oc.start_time,5,oc.live_status)),oc.live_status) AS lineState,
IFNULL((SELECT COUNT(*) FROM apply_r_grade_course WHERE course_id = oc.id),0) as learndCount, oc.address as address, oc.city as city, oc.direct_id as directId, oc.subtitle as subtitle, oc.current_price*10 as currentPrice, IFNULL((select count(*) from oe_criticize cc where cc.status = 1 and cc.course_id = oc.id ),0) as criticizeCount,
IFNULL((SELECT ROUND(AVG(cc.overall_level), 1) FROM oe_criticize cc where cc.status = 1 and cc.course_id = oc.id and cc.overall_level !=0 ),0) as startLevel, oc.lecturer as name, oc.lecturer_description as lecturerDescription, ou.small_head_photo as headImg, ou.name as heir, ou.vhall_id as vhallId, oc.live_source_type as liveSourceType FROM oe_course as oc inner join oe_user as ou on oc.user_lecturer_id = ou.id where oc.id = #{courseId} and oc.is_delete=0 and ou.status = 0 and ou.is_delete =0

select cai.img_path as smallImgPath, cai.title as gradeName, cai.user_id as userLecturerId, cai.`price` * 10 as currentPrice, cai.lecturer as name, if(cai.course_form=1,3,if(cai.course_form=3,4,if(cai.multimedia_type=1,1,2))) as type, cai.start_time as startTime, cai.end_time as endTime, cai.course_number as courseNumber, cai.address as address, cai.city as city, cai.`status` as applyStatus, cai.collection as collection, oc.id as id, oc.live_source_type as liveSourceType, if(oc.live_status = 2,if(DATE_SUB(now(),INTERVAL 30 MINUTE)>=oc.start_time,6,if( DATE_ADD(now(),INTERVAL 10 MINUTE)>=oc.start_time and now()<oc.start_time,4,if(DATE_ADD(now(),INTERVAL 2 HOUR)>=oc.start_time and now()<oc.start_time,5,oc.live_status))),oc.live_status) AS lineState, if(cai.`status`=1,(SELECT COUNT(*) FROM apply_r_grade_course WHERE course_id = oc.id),0) as learndCount, oc.direct_id as directId, if(now() > DATE_SUB(oc.start_time,INTERVAL 1 HOUR) and oc.start_time > now() ,1,0) as isLive from course_apply_info cai LEFT JOIN oe_course as oc on cai.id = oc.apply_id where cai.user_id = #{userId}
AND cai.`course_form` = #{courseForm}
AND cai.`multimedia_type` = #{multimediaType}
order by oc.release_time desc
